import logo from './logo.svg';
import './App.css';
import NavBar from './Navbar';
import { Routes, Route } from 'react-router-dom'
import { useState, useContext } from "react"
import Home from './Home';
import Companies from './Companies';
import CompanyJobs from './CompanyJobs';
import Jobs from './Jobs';
import LogIn from './LogIn';
import SignUp from './SignUp';
import JoblyApi from './api';

function App() {
  const [currentUser, setCurrentUser] = useState({ username: "", email: "", firstName: "", lastName: "" })
  const [token, setToken] = useState({ token: "" })

  const addUser = ({ user, token }) => {
    setToken(token);
    setCurrentUser({ username: user.username, email: user.email, firstName: user.firstName, lastName: user.lastName })
  }
  const logout = () => {
    setToken({ token: "" })
    setCurrentUser({ username: "", email: "", firstName: "", lastName: "" })
  }

  return (
    <div className="App">
      <NavBar currentUser={currentUser} />
      <Routes>
        <Route path="/" element={<Home currentUser={currentUser} />} />
        <Route path="/companies" element={<Companies />} />
        <Route path="/companies/:handle" element={<CompanyJobs />} />
        <Route path="/jobs" element={<Jobs />} />
        <Route path="/login" element={<LogIn addUser={addUser} />} />
        <Route path="/signup" element={<SignUp addUser={addUser} />} />
        <Route path="/profile" element={<Profile currentUser={currentUser} />} />
      </Routes>
    </div>
  );
}

export default App;
